CREATE DATABASE customer_behavior;
SELECT * FROM customer LIMIT 20;
-- What is the total revenue generated by male vs female customers?
SELECT `gender`, SUM(`purchase_amount`) as revenue FROM customer GROUP BY `gender`;
-- Which customers used a discount but still spent more than the average purchase amount?
SELECT `customer_id` , `purchase_amount` 
FROM customer 
WHERE discount_applied = 'Yes' and purchase_amount >= (SELECT AVG(`purchase_amount`) FROM customer);
-- Which are the top 5 products with the average review rating?
SELECT `item_purchased` , AVG(`review_rating`) AS avg_review 
FROM customer GROUP BY `item_purchased`
ORDER BY AVG(`review_rating`) DESC LIMIT 5;
-- Compare the average Purchase Amounts between standard and express shipping
SELECT `shipping_type`,AVG(`purchase_amount`) AS avg_purchase 
FROM customer WHERE `shipping_type` in ('Standard','Express')GROUP BY `shipping_type`
ORDER BY AVG(`purchase_amount`) DESC;
-- Do subscribed customers spend more? Compare averge spend and total revenue 
-- between subscribers and non sub-subscribers
SELECT `subscription_status`,
COUNT(`customer_id`) as total_customers,
ROUND(AVG(`purchase_amount`),2) as avg_spend,
ROUND(SUM(`purchase_amount`),2) as total_revenue
FROM customer
GROUP BY `subscription_status`
ORDER BY total_revenue, avg_spend DESC;
-- Which 5 products have the highest percentage of purchases with discount applied
SELECT `item_purchased`, 
ROUND(100 * SUM(CASE WHEN `discount_applied` = 'Yes' THEN 1 ELSE 0 END)/ COUNT(*),2) AS discount_rate
FROM customer 
GROUP BY `item_purchased` ORDER BY discount_rate DESC LIMIT 5;
-- Segment customers into New, Returning and loyal based on their total
-- number of previous purchases, and show the count of each segment
WITH `customer_type` AS (
SELECT `customer_id`, `previous_purchases`,
CASE
    WHEN `previous_purchases` = 1 THEN 'New'
    WHEN `previous_purchases` BETWEEN 2 AND 10 THEN 'Returning'
    ELSE 'Loyal'
    END AS customer_segment
FROM customer
)
SELECT customer_segment,COUNT(*) AS "Number of Customers"
FROM customer_type
GROUP BY customer_segment;
-- What are the top 3 most purchased product within each category?
WITH item_counts AS (
SELECT category,
item_purchased,
COUNT(customer_id) AS total_orders,
ROW_NUMBER() over(partition by category ORDER BY COUNT(customer_id) DESC) AS item_rank
FROM customer
GROUP BY category, item_purchased
)
SELECT item_rank, category, item_purchased, total_orders
FROM item_counts
WHERE item_rank <= 3;
-- Are customers who are repeat buyers (more than 5 previous purchases) also likely to subscribe?
SELECT subscription_status,
COUNT(customer_id) AS repeat_buyers
FROM customer
WHERE previous_purchases > 5
GROUP BY subscription_status;
-- What is the revenue contribution of each age group
SELECT age_group,
SUM(purchase_amount) AS total_revenue
FROM customer
GROUP BY age_group
ORDER BY total_revenue DESC;
